import React, { useContext } from 'react';
import { BrowserRouter as Router, Link, Route, Routes } from 'react-router-dom';
import './App.css';
import HomeScreen from './screens/HomeScreen';
import ProductScreen from './screens/ProductScreen';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import Badge from 'react-bootstrap/Badge';
import Container from 'react-bootstrap/Container';
import { LinkContainer } from 'react-router-bootstrap';
import { Store } from './Store';



function App() {
    const { state } = useContext(Store);
    const { cart } = state;
  return (
        <Router>
    <div className="d-flex flex-column site-container">
    <header>
        <Navbar bg="dark" variant="dark">
                <Container>
                        <LinkContainer to="/">
                        <Navbar.Brand>KukuPay Marketplace</Navbar.Brand>    
                        </LinkContainer>
                        <Nav className="me-auto">
                            {/* <Link to="/cart" className="nav-link">Cart {cart.cartItems.length > 0 && (
                                <Badge pill bg="danger">{cart.cartItems.reduce((a, c) => a + c.quantity, 0)}</Badge>
                            )}</Link> */}
                            {/* <Link to="/cart" className="nav-link">Cart 
                            </Link> */}
                        </Nav>
                </Container>
        </Navbar>

    </header>
    <main>
        <Container className="mt-3">
            <Routes>
            <Route path="/" element={<HomeScreen />} />
            <Route path="/product/:slug" element={<ProductScreen />} />

            </Routes>
        </Container>

    </main>
    <footer><div className="text-center">All rights reserved</div></footer>
</div>
        </Router>
  );
}

export default App;
