import { useContext, useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from "react-router-dom";
import Container from "react-bootstrap/esm/Container";
import Form from "react-bootstrap/esm/Form";
import Button from "react-bootstrap/esm/Button";
import { Helmet } from "react-helmet-async";
import Axios from 'axios';
import { Store } from "../Store";
import { toast } from 'react-toastify';
import { getError } from '../utils';

export default function SignupScreen(){
    const navigate = useNavigate();
    const { search } = useLocation();
    const redirectInUrl = new URLSearchParams(search).get('redirect');
    const redirect = redirectInUrl ? redirectInUrl : '/';

    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");

    const { state, dispatch: ctxDispatch } = useContext(Store);
    const {userInfo} = state;

    const submitHandler= async(e) => {
        e.preventDefault()
        try {
            const { data } = await Axios.post('/api/users/signup', {

                email,
                password
            })
            ctxDispatch({type: 'USER_SIGNIN', payload: data})
            localStorage.setItem('userInfo', JSON.stringify(data));
            navigate(redirect || '/');
        } catch(error) {
            toast.error(getError(error));
        }
    }

    useEffect(() => {
        if (userInfo) {
            navigate(redirect);
        }
    }, [navigate, redirect, userInfo]);
    return(
        <Container className="small-container">
            <Helmet>
                <title>Sign Up</title>
            </Helmet>
            <h1 className="my-3">Sign Up</h1>
            <Form onSubmit={submitHandler}>
                <Form.Group className="mb-3" controllId="name">
                <Form.Label>Name</Form.Label>
                <Form.Control type="name" required onChange={(e)=> setName(e.target.value)}/>
                </Form.Group>
                <Form.Group className="mb-3" controllId="email">
                <Form.Label>Email</Form.Label>
                <Form.Control type="email" required onChange={(e)=> setEmail(e.target.value)}/>
                </Form.Group>
                <Form.Group className="mb-3" controllId="password">
                <Form.Label>Password</Form.Label>
                <Form.Control type="password" required onChange={(e) => setPassword(e.target.value)}/>
                </Form.Group>
                <Form.Group className="mb-3" controllId="confirmPassword">
                <Form.Label>Confirm Password</Form.Label>
                <Form.Control type="password" required onChange={(e) => setConfirmPassword(e.target.value)}/>
                </Form.Group>
                <div className="mb-3">
                    <Button type="submit">Sign Up</Button>
                </div>
                <div className="mb-3">Aready have an account?{' '}
                <Link to={`/signup?redirect=${redirect}`}>Sign-In</Link>
                </div>
            </Form>
        </Container>
    )
}
